import React from 'react';
import styled from 'styled-components'
import Post from './components/Post/Post';

const MainContainer = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
`


  class App extends React.Component {
    state = {
      userName:  "Paulinha",
      userPicture: 'https://picsum.photos/50/50',
      userPost: 'https://picsum.photos/200/150'
    }

    adicionarPost = () => {
      const novaLista = [...this.state.userPost];
      novaLista.push(this.state.userPost);
      this.setState({
        userPost: novaLista
      })

    }


    render() {
      return (
        <MainContainer>
          <Post
            nomeUsuario={this.state.userName}
            fotoUsuario={this.state.userPicture}
            fotoPost={this.state.userPost}
          />

          <Post
            nomeUsuario={'Rodrigo'}
            fotoUsuario={'https://picsum.photos/50/51'}
            fotoPost={'https://picsum.photos/200/151'}
          />

          <Post
            nomeUsuario={'Rafael'}
            fotoUsuario={'https://picsum.photos/50/52'}
            fotoPost={'https://picsum.photos/200/152'}
          />

          <button onClick={this.adicionarPost}>Post Novo</button>
        </MainContainer>
      );
    }
  }

  export default App;
