import React from 'react';
import styled from 'styled-components'
import Post from './components/Post/Post';

const MainContainer = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
`


  class App extends React.Component {
    state = {
    userName:  '', 
    userPicture: '',
    userPost: '',
    posts: [

      {
        nomeUsuario:'Paulinha',
        fotoUsuario:'https://picsum.photos/50/50',
        fotoPost:'https://picsum.photos/200/150'
      },

      {
        nomeUsuario:'Rodrigo',
        fotoUsuario:'https://picsum.photos/50/51',
        fotoPost:'https://picsum.photos/200/151'
      },

      { 
        nomeUsuario:'Rafael',
        fotoUsuario:'https://picsum.photos/50/52',
        fotoPost:'https://picsum.photos/200/152'
      }

    ]
    }



    adicionarPost = () => {
      const novaLista = [...this.state.userPost];

      novaLista.push(this.state.userName, this.state.userPicture, this.state.userPost);
      this.setState({
        posts: novaLista
      })

    }


    render() {
      return (
            <MainContainer>
      <input value={this.state.userName} 
             onChange={this.onChangeUserName}
             placeholder={"nome"}/>


      <input value={this.state.userPicture} 
             onChange={this.onChangeUserPicture}
             placeholder={"foto"}/>


      <input value={this.state.userPost} 
             onChange={this.onChangeUserPost}
             placeholder={"post"}/>

          {this.state.posts.map( post => 
           {            
             return (
                <Post 
                  nomeUsuario={post.nomeUsuario}
                  fotoUsuario={post.fotoUsuario}
                  fotoPost={post.fotoPost}
                />
             )
           } 
          )}
        </MainContainer>

      );
    }
  }

  export default App;
